import React, { Component } from 'react';
import { Container, Row, Col, Form, Spinner, Button } from 'react-bootstrap';
import { connect } from 'react-redux';
import { Redirect } from 'react-router-dom';
import { Formik } from 'formik';
import * as yup from 'yup';

import classes from './Create.module.css';
import * as actions from '../../../../../../store/actions/index';

import { workoutSessionForm, formInputChanged } from '../../../../../../shared/form-utility';
import { formObjectToArray, mapKeyToValue, tranformPascalCaseToUnderscoreCase } from '../../../../../../shared/utility';
import Aux from '../../../../../../hoc/Aux/Aux';

class WorkoutSessionCreate extends Component {
    state = {
        form: workoutSessionForm
    }

    inputChangedHandler = ( event, key ) => {
        this.setState({ form: formInputChanged( this.state.form, event, key ) });
    }

    onCreateWorkoutSessionHandler = event => {
        this.props.onCreateWorkoutSession({
            data: tranformPascalCaseToUnderscoreCase( mapKeyToValue( this.state.form ) ),
            id: this.props.match.params.id,
            token: this.props.token
        });
    }

    render () {

        const btnChild = this.props.loading ? (
           <Aux>
               <Spinner
               as="span"
               animation="grow"
               size="sm"
               role="status"
               aria-hidden="true"
               />
               Loading...
           </Aux>
        ): 'submit';

        const schema = yup.object({
            weight: yup.number().required(),
            weight_unit: yup.string().required(),
            waist: yup.number().required(),
            waist_unit: yup.string().required(),
        });

        const form = this.props.view ? (<Redirect to={this.props.redirect} />) : (

                <Formik
                    validationSchema={schema}
                    onSubmit={this.onCreateWorkoutHandler}
                    initialValues={{
                        weight: '',
                        weight_unit: '',
                        waist: '',
                        waist_unit: ''
                    }}
                >
                    {({
                        handleSubmit,
                        handleChange,
                        handleBlur,
                        values,
                        touched,
                        isValid,
                        errors,
                    }) => (
                        <Form noValidate onSubmit={handleSubmit}>
                            <Form.Group as={Row} controlId="weight">
                             <Form.Label>Weight</Form.Label>

                                <Form.Group as={Col} controlId="weight">

                                      <Form.Control
                                        type="text"
                                        placeholder="Weight"
                                        name="weight"
                                        value={values.weight}
                                        onChange={handleChange}
                                        isInvalid={!!errors.weight}
                                      />
                                      <Form.Control.Feedback type="invalid">
                                        {errors.weight}
                                      </Form.Control.Feedback>
                                </Form.Group>
                                  <Form.Group as={Col} controlId="exampleForm.ControlSelect1">
                                      <Form.Label> </Form.Label>
                                      <Form.Control as="select">
                                        <option>lbs</option>
                                        <option>kg</option>
                                      </Form.Control>
                                </Form.Group>

                            </Form.Group>

                            <Form.Group as={Col} controlId="waist">
                              <Form.Label>Waist</Form.Label>
                              <Form.Control
                                type="text"
                                placeholder="Waist"
                                name="waist"
                                value={values.waist}
                                onChange={handleChange}
                                isInvalid={!!errors.waist}
                              />
                              <Form.Control.Feedback type="invalid">
                                {errors.waist}
                              </Form.Control.Feedback>
                            </Form.Group>
                            <div className={classes.Buttons}>
                                <Button variant='danger' type="submit">{ btnChild }</Button>
                                <Button variant='secondary' disabled={this.props.loading}>Cancel</Button>
                            </div>
                        </Form>
                    )}
                </Formik>
            )
        return form;
    }
};

const mapStateToProps = state => {
    return {
        token: state.signin.token,
        redirect: state.workouts.redirect
    }
}

const mapDispatchToProps = dispatch => {
    return {
        onCreateWorkoutSession: data => dispatch( actions.createWorkoutSession( data ))
    };
};


export default connect( mapStateToProps , mapDispatchToProps) ( WorkoutSessionCreate );
