import React from 'react';
import { connect } from 'react-redux';
import * as actions from '../../../store/actions/index';
import Input from '../../UI/Input/Input';

class Signup extends Component {
    state = {
        form: {
            email: {
                type: 'input',
                config: {
                    type: 'email',
                    placeholder: 'email'
                },
                value: '',
                label: 'Email'
            },
            password: {
                type: 'input',
                config: {
                    type: 'password',
                    placeholder: 'password'
                },
                value: '',
                label: 'Password'
            }
        }
    }
    render () {
        console.log(props.isAuth);
        let formElements = [];
        for ( let key in this.state.form) {
            formElements.push({
                key: key,
                settings: this.state.form[key]
            })
        }

        let form = formElements.map( ele => {
            <Input
                key={ele.key}
                type={ele.settings.type}
                config={ele.settings.config}
                value={ele.settings.value}
                label={ele.settings.label}
                />

        });

        return (
            <div>
                <form>
                    {form}
                </form>
            </div>
        );
    }
};

const mapStateToProps = state => {
    return {
        isAuth: state.signin.token !== null
    };
};

const mapDispatchToProps = dispatch => {
    return {
        onSignup: data => dispatch( actions.signup( data ) )
    }
}

export default connect( mapStateToProps, mapDispatchToProps)( signup );
