import axios from "axios";
import Cookie from 'js-cookie';

import { REQUEST_SUCCESS, SIGNUP_SHOW, REQUEST_FAILED } from './actionTypes';

export const requestSuccess = data => {
    return {
        account: data.data,
        type: REQUEST_SUCCESS,
        loading: false,
        hide: true,
        redirect: data.redirect,
        toast: data.toast,
        errors: []
    };
};

const requestFailed = err => {
    let errors = [];
    switch( err.status ) {
        case 400:
            errors = err.data.errors;
            break;
        case 500:
            errors.push({ message: err.statusText})
            errors = errors;
    }
    return {
        type: REQUEST_FAILED,
        errors: errors,
        redirect: '/'
    };
}

export const signup = data => {

    return dispatch => {

        axios.post('/api/accounts/signup', data)
        .then( res => {
            Cookie.set('account', res.data.account);
            dispatch( requestSuccess({ 
                data: res.data.account,
                toast: 'Account created successfully. Login to access account',
                redirect: '/'
            }) );
        })
        .catch( err => {
            dispatch( requestFailed( err.response ) );
        });
    };
};

export const showSignup = currentState => {
    return {
        type: SIGNUP_SHOW,
        hide: !currentState
    }
}
