import React, { Component } from 'react';
import Table from 'react-bootstrap/Table';
import Button from 'react-bootstrap/Button';

import { transformUnderscoreCaseToSentenceCase, excludeKeyFromObj } from '../../../shared/utility';

import classes from './Table.module.css';

class CustomTable extends Component {

    onClickedHandler = (key,values ) => {
        console.log(222);
        console.log(key);
        console.log(values);
        this.props.viewClicked( key );
    }

    renderTableData = (data, key) => {
        return data.map( values => {
            let pk = values[key];

            const tableData = Object.keys( excludeKeyFromObj( values, key ) ).map( id => {
                return (
                    <td key={id}>{ values[id] }</td>
                )
            });
            return (
                <tr key={ key + values[key] }>
                    {tableData}
                    <td key={'action' + key}>
                        <Button variant='link' key={pk} onClick={ () => this.onClickedHandler( key, data ) }>view</Button>
                    </td>
                </tr>
            );
        });
    };

    renderTableColumnNames = (data, key) => {
        return data.slice(0,1).map( values => {
            const tableColumnNames = Object.keys( excludeKeyFromObj( values, key ) ).map( key => {
                return (
                    <th key={key}>{ transformUnderscoreCaseToSentenceCase( key ) }</th>
                    )
                });
            return (
                <tr key={ key + values[key] }>
                    {tableColumnNames}
                    <th key={'action' + values[key]}></th>
                </tr>
            );
        });
    }

    render () {

        const table = this.props.data && this.props.data.length > 0 ? (
            <Table striped bordered hover responsive="sm">
                <thead>
                    {this.renderTableColumnNames([...this.props.data], this.props.keyValue)}
                </thead>
                <tbody>
                    {this.renderTableData([...this.props.data], this.props.keyValue)}
                </tbody>
            </Table>
        ) : <span>Nothing to display!</span>

        return (
            <div className={classes.Table}>
                {this.props.tableHeader}
                {table}
            </div>
        );
    }
}


export default CustomTable;
