export const updateObject = ( old, updated = {}) => {
    return { ...old, ...updated };
};

export const mapKeyToValue = data => {
    if (data) {
        return Object.keys(data)
            .map( x => {
                return { [x] : data[x].value };
            })
            .reduce( (result, current) => {
                return Object.assign(result, current);
            }, {}) ;
    }
}
