import { connect } from 'react-redux';
import React, { Component } from 'react';
import { Redirect } from 'react-router-dom';
import { Form, Alert, Button, Spinner, Col, InputGroup } from 'react-bootstrap';
import { Formik } from 'formik';
import * as yup from 'yup';

import classes from './Signin.module.css';

import { signinForm, formInputChanged} from '../../../shared/form-utility';
import { formObjectToArray, mapKeyToValue } from '../../../shared/utility';
import Aux from '../../../hoc/Aux/Aux';

import * as actions from '../../../store/actions/index';

class Signin extends Component {
    state = {
        form: signinForm,
        validated: false
    }

    componentDidMount () {
        this.props.onCheckAuth();
        if ( this.props.isAuth ) {
            this.props.history.push(this.props.redirect);
            return;
        }
        this.props.onSigninShow();
    }

    inputChangedHandler = ( event, key ) => {
        this.setState({ form: formInputChanged( this.state.form, event, key) });
    }

    onSigninHandler = event => {
        const form = event.currentTarget;
        console.log(form);

        if ( form.checkValidity() == false) {
            event.preventDefault();
            event.stopPropagation();
        }

        this.setState({ validated: true });
        // this.props.onSignin( mapKeyToValue( this.state.form ));
        // return <Redirect to={ this.props.redirect} />;
    }

    render () {
        const errors = this.props.errors && this.props.errors.length > 0 ? (
            <Alert variant='danger'>
                {
                    this.props.errors.map( ( err, key ) => <span key={ key }>{ err.message } <br/></span>)
                }
            </Alert>
        ): null;

        let form = formObjectToArray( this.state.form ).map( ele => {
            return (
                <Form.Group as={Col} controlId={ele.key} key={ele.key}>
                    <Form.Label>{ ele.settings.label }</Form.Label>
                    <Form.Control
                        required={ ele.settings.config.required }
                        type={ ele.settings.config.type }
                        placeholder={ ele.settings.config.placeholder}
                        onChange={ event => this.inputChangedHandler( event, ele.key)}/>
                    <Form.Control.Feedback type="invalid"> Field is required. </Form.Control.Feedback>
                </Form.Group>
            );

        });

        let redirect = this.props.isAuth ? <Redirect to={this.props.redirect} /> : null;

        const signinBtnChild = this.props.loading ? (
            <Aux>
                <Spinner
                as="span"
                animation="grow"
                size="sm"
                role="status"
                aria-hidden="true"
                />
                Loading...
            </Aux>
        ): 'Signup';

        const schema = yup.object({
            firstName: yup.string().required(),
            lastName: yup.string().required(),
            username: yup.string().required(),
            city: yup.string().required(),
            state: yup.string().required(),
            zip: yup.string().required(),
            terms: yup.bool().required(),
        });

        return (
        //     <div className={classes.Signin}>
        //         {errors}
        //         <Form noValidate validated={this.state.validated} className={classes.Form} onSubmit={this.onSigninHandler}>
        //             {redirect}
        //             <Form.Group controlId="email">
        //                 <Form.Label>Email</Form.Label>
        //                 <Form.Control
        //                     required
        //                     type="text"
        //                     placeholder="Email"
        //                 />
        //               <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
        //             </Form.Group>
        //             <Form.Group controlId="password">
        //                 <Form.Label>Password</Form.Label>
        //                 <Form.Control
        //                     type="text"
        //                     placeholder="Username"
        //                     aria-describedby="inputGroupPrepend"
        //                     required
        //                 />
        //                 <Form.Control.Feedback type="invalid">
        //                     Please enter a valid password
        //                 </Form.Control.Feedback>
        //             </Form.Group>
        //             <div className={classes.Buttons}>
        //                 <Button variant='danger' type="submit">{ signinBtnChild }</Button>
        //                 {/* <Button variant='secondary' disabled={this.props.loading}>Cancel</Button> */}
        //             </div>
        //         </Form>
        //     </div>
        // );
        <Formik
            validationSchema={schema}
            onSubmit={console.log}
            initialValues={{
            firstName: 'Mark',
            lastName: 'Otto',
            }}
        >
        {({
            handleSubmit,
            handleChange,
            handleBlur,
            values,
            touched,
            isValid,
            errors,
        }) => (
        <Form noValidate onSubmit={handleSubmit}>
          <Form.Row>
            <Form.Group as={Col} md="4" controlId="validationFormik01">
              <Form.Label>First name</Form.Label>
              <Form.Control
                type="text"
                name="firstName"
                value={values.firstName}
                onChange={handleChange}
                isValid={touched.firstName && !errors.firstName}
              />
              <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
            </Form.Group>
            <Form.Group as={Col} md="4" controlId="validationFormik02">
              <Form.Label>Last name</Form.Label>
              <Form.Control
                type="text"
                name="lastName"
                value={values.lastName}
                onChange={handleChange}
                isValid={touched.lastName && !errors.lastName}
              />

              <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
            </Form.Group>
            <Form.Group as={Col} md="4" controlId="validationFormikUsername">
              <Form.Label>Username</Form.Label>
              <InputGroup>
                <InputGroup.Prepend>
                  <InputGroup.Text id="inputGroupPrepend">@</InputGroup.Text>
                </InputGroup.Prepend>
                <Form.Control
                  type="text"
                  placeholder="Username"
                  aria-describedby="inputGroupPrepend"
                  name="username"
                  value={values.username}
                  onChange={handleChange}
                  isInvalid={!!errors.username}
                />
                <Form.Control.Feedback type="invalid">
                  {errors.username}
                </Form.Control.Feedback>
              </InputGroup>
            </Form.Group>
          </Form.Row>
          <Form.Row>
            <Form.Group as={Col} md="6" controlId="validationFormik03">
              <Form.Label>City</Form.Label>
              <Form.Control
                type="text"
                placeholder="City"
                name="city"
                value={values.city}
                onChange={handleChange}
                isInvalid={!!errors.city}
              />

              <Form.Control.Feedback type="invalid">
                {errors.city}
              </Form.Control.Feedback>
            </Form.Group>
            <Form.Group as={Col} md="3" controlId="validationFormik04">
              <Form.Label>State</Form.Label>
              <Form.Control
                type="text"
                placeholder="State"
                name="state"
                value={values.state}
                onChange={handleChange}
                isInvalid={!!errors.state}
              />
              <Form.Control.Feedback type="invalid">
                {errors.state}
              </Form.Control.Feedback>
            </Form.Group>
            <Form.Group as={Col} md="3" controlId="validationFormik05">
              <Form.Label>Zip</Form.Label>
              <Form.Control
                type="text"
                placeholder="Zip"
                name="zip"
                value={values.zip}
                onChange={handleChange}
                isInvalid={!!errors.zip}
              />

              <Form.Control.Feedback type="invalid">
                {errors.zip}
              </Form.Control.Feedback>
            </Form.Group>
          </Form.Row>
          <Form.Group>
            <Form.Check
              required
              name="terms"
              label="Agree to terms and conditions"
              onChange={handleChange}
              isInvalid={!!errors.terms}
              feedback={errors.terms}
              id="validationFormik0"
            />
          </Form.Group>
          <Button type="submit">Submit form</Button>
        </Form>
      )}
    </Formik>);
    };
};

const mapStateToProps = state => {
    console.log( state );
    return {
        isAuth: state.signin.token !== null,
        redirect: state.signin.redirect,
        errors: state.signin.errors,
        loading: state.signin.loading
    };
};

const mapDispatchToProps = dispatch => {
    return {
        onSignin : data => dispatch( actions.signin ( data ) ),
        onSigninShow: () => dispatch( actions.showSignin() ),
        onCheckAuth: () => dispatch( actions.checkSignedIn() )
    };
};

export default connect( mapStateToProps, mapDispatchToProps) ( Signin );
