import React, { Component } from 'react';
import Form from 'react-bootstrap/Form';
import Col from 'react-bootstrap/Col';
import Button from 'react-bootstrap/Button';

import classes from './Create.module.css';

import { workoutSessionForm, formInputChanged, formObjInputChanged } from '../../../../../../shared/form-utility';
import { formObjectToArray } from '../../../../../../shared/utility';

class WorkoutSessionCreate extends Component {
    state = {
        form: workoutSessionForm
    }

    inputChangedHandler = ( event, key ) => {
        const formChanged = key in ['weight', 'waist'] ? formObjInputChanged( this.state.form, event, key ) : formInputChanged( this.state.form, event, key )
        this.setState({ form: formInputChanged( this.state.form, event, key) });
        console.log(formChanged);
    }

    onCreateWorkoutSessionHandler = event => {
        console.log(123123);
    } 

    render () {
        let form = formObjectToArray( this.state.form ).map( ele => {
            const label = ele.settings.objValue ? ele.settings.value.label : ele.settings.label;
            const placeholder = ele.settings.objValue ? ele.settings.value.config.placeholder : ele.settings.config.placeholder;
            const valueId = ele.settings.objValue ? ele.key+'Value' : ele.key;
            const unitId = ele.settings.objValue ? ele.key+'Unit' : ele.key;
            const type = ele.settings.objValue ? ele.settings.value.config.type : ele.settings.config.type;

            const unit = ele.settings.objValue ? (
                <Col sm={4} className="my-1">
                    <Form.Label htmlFor={ele.key} srOnly>
                        unit
                    </Form.Label>
                    <Form.Control 
                        as="select" 
                        defaultValue="Choose..." 
                        id={ unitId }
                        onChange={ event => this.inputChangedHandler( event, ele.key )} >
                        <option>Choose unit...</option>
                        <option value="cm">cm</option>
                        <option value="inch">inch</option>
                    </Form.Control>
                </Col>
            ): null;
            return (
                <Form.Row className="align-items-center" key={ valueId }>
                    <Col xs="auto" className="my-1">
                        <Form.Label> { label } </Form.Label>
                    </Col>
                    <Col sm={6} className="my-1">
                        <Form.Label htmlFor={ele.key} srOnly>
                        { label }
                        </Form.Label>
                        <Form.Control 
                            type={ type }
                            id={ele.key}
                            placeholder={placeholder}
                            onChange={ event => this.inputChangedHandler( event, ele.key ) }/>
                    </Col>
                    { unit }

                </Form.Row>
            );
        });

        return (
        <div className={classes.Create}>
            <Form>
                { form }
                <Button variant='danger' onClick={this.onCreateWorkoutSessionHandler}>
                    Submit
                </Button>
            </Form>
        </div>
        );
    }
};

export default WorkoutSessionCreate;
