import React, { Component } from 'react';

import Signup from '../../../components/Auth/Signup/Signup';
import Signin from '../../../components/Auth/Signin/Signin';
import classes from './Home.module.css';


class Home extends Component {
    state = {
        showSignup: false
    }

    showSigninHandler = ( event ) => {
        event.preventDefault();
        this.setState({ showSignup: !showSignup });
    }
    render () {
        const auth = !this.state.showSignup ? <Signin /> : <Signup /> ;
        const text = !this.state.showSignup ? "Already have an account?": "Don't have an account yet?" ;
        return (
            <div className={classes.Home}>
                { auth }
                <div className={classes.ShowSignin}>                
                    <a href="" onClick={this.showSigninHandler}>{ text }</a>
                </div>
            </div>

        );
    };
};

export default Home;
