import React from 'react';
import { connect } from 'react-redux';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Accordion from 'react-bootstrap/Accordion';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import moment from 'moment';


import classes from './View.module.css';

const workoutview = props => {
    console.log(props.match.params);
    console.log(props.workouts);
    const workout = props.workout
    // const sessions = props.workout && props.workout.sessions.length > 0 ? props.workout.session : [];
    const sessionData = props.workout.sessions.map ( session => {
        return ( <Col md='3'>
                    <Accordion defaultActiveKey={ session._id }>
                        <Card key={session._id}>
                            <Card.Header>
                            <Accordion.Toggle as={Button} variant="link" eventKey={ session._id}>
                                {session.start}
                            </Accordion.Toggle>
                            </Card.Header>
                            <Accordion.Collapse eventKey={ session._id}>
                            <Card.Body>
                                <ul>
                                    <li>{ session.weight } ( { session.weight_measure } )</li>
                                    <li>{moment( session.start ).format('MMMM Do YYYY')} </li>
                                    <li>{moment( session.start ).format('h:mm:ss a')} - {moment( session.end ).format('h:mm:ss a')}</li>
                                    <li>
                                        <Button variant='link' href={ session.publicUrl }>link</Button>
                                    </li>
                                </ul>
                            </Card.Body>
                            </Accordion.Collapse>
                        </Card>
                    </Accordion>
                </Col>
            );
    });

    return(
        <Container className={classes.View} fluid>
            <Row>
                <div className={classes.Title}>
                    <h3>{props.workout.name}</h3>
                    <span>{props.workout.description}</span>
                    <span>{props.workout.created_at}</span>
                </div>
            </Row>
            <Row>
                <div className={classes.Sessions}>
                    <h5>Sessions</h5>
                    { sessionData }
                </div>
            </Row>
        </Container>
     );
};

const mapStateToProps = state => {
    console.log(state);
    return {
        workout: state.workouts.view
    }
}

export default connect(mapStateToProps)( workoutview );
