import axios from 'axios';
import Cookie from 'js-cookie';

import { SIGNIN_SUCCESS, SIGNOUT_SUCCESS, REQUEST_FAILED, REQUEST_STARTED, RENDER_COMPONENT } from "./actionTypes"

const signinSuccess = token => {
    return {
        type: SIGNIN_SUCCESS,
        token: token,
        redirect: '/workouts',
        loading: false,
        errors: [],
    };
};


const requestStarted = () => {
    return {
        loading: true,
        type: REQUEST_STARTED,
        errors: [],
    }
}

const requestFailed = err => {
    let errors = [];
    switch( err.status ) {
        case 400:
            errors = err.data.errors;
            break;
        case 500:
            errors.push({ message: err.statusText})
            errors = errors;
    }
    return {
        type: REQUEST_FAILED,
        errors: errors,
        redirect: '/',
        loading: false
    };
}

export const signin = data => {
    return dispatch => {
        dispatch( requestStarted() );

        axios.post('/api/accounts/signin', data)
        .then( res => {
            if( 'token' in res.data ) Cookie.set('token', res.data.token);
            dispatch( signinSuccess( res.data.token ));
        })
        .catch( err => {
            dispatch( requestFailed( err.response ) );
        })
    }
}

export const checkSignedIn = () => {
    return dispatch => {
        const token = Cookie.get('token');
        if (!token) {
            dispatch( signout() );
        } else {
            dispatch( signinSuccess(token) );
        }
    };
};

export const signout = () => {
    Cookie.remove('token');
    return {
        type: SIGNOUT_SUCCESS,
        redirect: '/'
    };
};

export const showSignin = () => {
    return dispatch => {
        dispatch({
            errors: [],
            type: RENDER_SIGNIN_COMPONENT
        });
    };
};
