import React, { Component } from 'react';
import { connect } from 'react-redux';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Accordion from 'react-bootstrap/Accordion';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import moment from 'moment';

import * as actions from '../../../../store/actions/index';


import classes from './View.module.css';

class WorkoutView extends Component {
    sessionCreateHandler = pk => {
        this.props.history.push('/workouts/'+pk+'/session/create');
        <Redirect to={ '/workouts/'+pk+'/session/create' } />
    }
    render () {
        const sessionData = this.props.workout.sessions.map ( session => {
            return ( <Col md='3' key={session._id}>
                        <Accordion defaultActiveKey={ session._id }>
                            <Card>
                                <Card.Header>
                                <Accordion.Toggle as={Button} variant="link" eventKey={ session._id}>
                                {moment( session.start ).format('MMMM Do YYYY')}
                                </Accordion.Toggle>
                                </Card.Header>
                                <Accordion.Collapse eventKey={ session._id}>
                                <Card.Body>
                                    <ul>
                                        <li>{ session.weight } ( { session.weight_measure } )</li>
                                        <li>{moment( session.start ).format('HH:mm:ss')} - {moment( session.end ).format('Hh:mm:ss')}</li>
                                        <li><Button variant='link' href={ session.publicUrl }>link</Button></li>
                                    </ul>
                                </Card.Body>
                                </Accordion.Collapse>
                            </Card>
                        </Accordion>
                    </Col>
                );
        });

        return(
            <Container className={classes.View} fluid>
                <Row>
                    <Col sm='12'>
                        <h4>{this.props.workout.name}</h4>
                        <span>{this.props.workout.description}</span>    
                    </Col>
                    <Col sm='12'>
                        <Button variant='primary' onClick={ () => this.sessionCreateHandler(this.props.workout._id)}> &#43; session</Button>
                    </Col>
                </Row>
                <Row>
                    <div className={classes.Sessions}>
                        <h5 className={classes.SessionTitle}>Sessions</h5>
                        { sessionData }
                    </div>
                </Row>
            </Container>
         );
    }
};

const mapStateToProps = state => {
    return {
        workout: state.workouts.view
    }
}

const mapDispatchToProps = dispatch => {
    return {
        // onCreateSession: dispatch( actions.createSession() )
    }
}

export default connect(mapStateToProps, mapDispatchToProps)( WorkoutView );
