import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Redirect } from 'react-router-dom';

import classes from './List.module.css';

import * as actions from '../../../../store/actions/index';
import Table from '../../../UI/Table/Table';

class WorkoutList extends Component {

    componentDidMount () {
        if ( this.props.isAuth ) {
            this.props.history.push(this.props.redirect)
            this.props.onFetchWorkouts( this.props.token );
        }
    }
    viewHandler = pk => {
        this.props.history.push('workouts/'+pk);
        return <Redirect to={'workouts/'+pk} />
    }

    render () {
        console.log(this.props.workouts);
        const exclude = ['sessions', '__v', '_account'];
        const copiedWorkouts = this.props.workouts && this.props.workouts.length > 0 ? [...this.props.workouts] : [];
        for ( let value of copiedWorkouts ) {
            for ( let x of exclude ) {
                delete value[x];
            }
        }

        return (
            <div className={classes.List}>
                <Redirect to={this.props.redirect} />
                <Table data={copiedWorkouts} keyValue='_id' viewClicked={this.viewHandler} />
            </div>
        );
    };
};

const mapStateToProps = state => {
    console.log(state);
    return {
        isAuth: state.signin.token !== null,
        token: state.signin.token,
        redirect: state.signin.redirect,
        workouts: state.workouts.list
    }
}

const mapDispatchToProps  = dispatch => {
    return {
        onFetchWorkouts: token => dispatch( actions.fetchWorkouts( token ) ),
    };
};

export default connect( mapStateToProps, mapDispatchToProps )( WorkoutList );
