import { updateObject } from './utility';

const email = {
    type: 'input',
        config: {
            type: 'email',
            placeholder: 'email'
        },
        value: '',
        label: 'Email'
};

const  password = {
    type: 'input',
    config: {
        type: 'password',
        placeholder: 'password'
    },
    value: '',
    label: 'Password'
};

const  repeat_password = {
    type: 'input',
    config: {
        type: 'password',
        placeholder: 'confirm password'
    },
    value: '',
    label: 'Confirm Password'
};

const measure = display => {
    return {
        type: 'input',
        config: {
            type: 'number',
            placeholder: display
        },
        value: '',
        label: display
    }
}

const unit = options => {
    return {
        type: 'select',
        config: {
            type: 'select',
            options: options
        }
    }
}

const heaviness = {
    lbs: 'lbs',
    kg: 'kg'
}

const thickness = {
    lbs: 'cm',
    kg: 'inch'
}

export const workoutSessionForm = {
    weight:  {
        objValue: true,
        value: measure('Weight'),
        unit: unit(heaviness)
    },
    waist: {
        objValue: true,
        value: measure('Waist'),
        unit: unit(thickness)
    }
};

export const signinForm = {
    email: email,
    password: password
};

export const signupForm = {
    email: email,
    password: password,
    repeat_password: repeat_password
}

export const formInputChanged = ( form, event, key) => {
    console.log(form);
    console.log(event);
    console.log(key);
    const updatedFormElement = updateObject(form[key], { value: event.target.value});
    return updateObject(form, { [key]: updatedFormElement });
}
