import React from 'react';

import classes from './Table.module.css';

const renderTableData = (data, key) => {
    return data.map( values => {
        console.log(values);
        const tableData = Object.keys(values).map( key => {
            return (
                <td key={key}></td>
            )
        });
        return (
            <tr key={values[key]}>
                {tableData}
            </tr>
        );
    });
};

const renderTableColumnNames = (data, key) => {
    return data.slice(0,1).map( values => {
        const tableColumnNames = Object.keys(values).map( key => {
            return (
                <th key={key}>{ key.charAt(0).toUpperCase() + key.slice(1) }</th>
            )
        });
        return (
            <tr key={values[key]}>
                {tableColumnNames}
            </tr>
        );
    });
}

const table = props => {
    if( props.data && props.data.length > 0 )   {
        console.log( renderTableData(props.data, props.keyValue) );
        console.log( renderTableColumnNames(props.data, props.keyValue));
    };
    const table = props.data && props.data.length > 0 ? (
        <table>
            <thead>
                {/* {this.renderTableColumnNames(this.props.data, this.props.keyValue)} */}
            </thead>
            <tbody>
                {/* {renderTableData(props.data, props.keyValue)} */}
            </tbody>
        </table>
    ) : <span>Nothing to display!</span>

    return (
        <div className={classes.Table}>
            {props.tableHeader}
            {table}
        </div>
    );
};

export default table;
