import { connect } from 'react-redux';
import React, { Component } from 'react';

import { signinForm, formInputChanged} from '../../../shared/form-utility';
import { formObjectToArray, mapKeyToValue } from '../../../shared/utility';

class Signin extends Component {
    state = {
        form: signinForm
    }
    inputChangedHandler = ( event, key ) => {
        this.setState({ form: formInputChanged( this.state.form, event, key) });
    }
    onSigninHandler = ( event ) => {
        event.preventDefault();
        this.props.onSignin( mapKeyToValue( this.state.form ));
    }
    render () {
        const formElements = formObjectToArray();
        let form = formElements.map( ele => {
            return <Input
                key={ele.key}
                type={ele.settings.type}
                config={ele.settings.config}
                value={ele.settings.value}
                label={ele.settings.label}
                changed={ event => this.inputChangedHandler( event, ele.key)}
                />
        })

        return (
            <div>
                <form>
                    {form}
                    <div className={classes.Buttons}>
                        <Button type='Success' clicked={this.onSigninHandler}>Signin</Button>
                        <Button type='Cancel'>Cancel</Button>
                    </div>
                </form>
            </div>
        );
    };
};

mapStateToProps = state => {
    return {
        isAuth: state.signin.token !== null
    };
};

mapDispatchToProps = dispatch => {
    return {
        onSignin : ( data ) => dispatch( signin ( data ) )
    };
};

export default connect( mapStateToProps, mapDispatchToProps) ( Signin );
