import axios from "axios";
import Cookie from 'js-cookie';

import { SIGNUP_SUCCESS, SIGNUP_START, SIGNUP_SHOW, REQUEST_FAILED } from './actionTypes';

export const signupSuccess = data => {
    return {
        account: data,
        type: SIGNUP_SUCCESS,
        loading: false,
        hide: true
    };
};

const requestFailed = err => {
    console.log(err);
    let errors = [];
    switch( err.status ) {
        case 400:
            errors = err.data.errors;
            break;
        case 500:
            errors = errors.push({ message: status.statusText});
    }
    return {
        type: REQUEST_FAILED,
        errors: err.data.errors,
        redirect: '/'
    };
}

export const signup = data => {

    return dispatch => {

        axios.post('/api/accounts/signup', data)
        .then( res => {
            Cookie.set('account', res.data.account);
            dispatch( signupSuccess(res.data.account) );
        })
        .catch( err => {
            dispatch( requestFailed( err.response ) );
        });
    };
};

export const showSignup = currentState => {
    return {
        type: SIGNUP_SHOW,
        hide: !currentState
    }
}
