import React from 'react';

import classes from './Input.module.css';

const input = props => {
    let input = null;

    switch ( props.type ) {
        case 'input':
            input = <input className={classes.Element}/>
        case 'select':
            input = <select className={classes.Element}>
                <option>1</option>
                <option>2</option>
                <option>3</option>
            </select>
    }

    return (
        <div className={classes.Input}>
            <label className={classes.Label}>Label</label>
            {input}
        </div>
    );
};

export default input;
