import React from 'react';
import { connect } from 'react-redux';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Accordion from 'react-bootstrap/Accordion';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import moment from 'moment';


import classes from './View.module.css';

const workoutview = props => {
    const sessionData = props.workout.sessions.map ( session => {
        return ( <Col md='3' key={session._id}>
                    <Accordion defaultActiveKey={ session._id }>
                        <Card>
                            <Card.Header>
                            <Accordion.Toggle as={Button} variant="link" eventKey={ session._id}>
                            {moment( session.start ).format('MMMM Do YYYY')}
                            </Accordion.Toggle>
                            </Card.Header>
                            <Accordion.Collapse eventKey={ session._id}>
                            <Card.Body>
                                <ul>
                                    <li>{ session.weight } ( { session.weight_measure } )</li>
                                    <li>{moment( session.start ).format('HH:mm:ss')} - {moment( session.end ).format('Hh:mm:ss')}</li>
                                    <li><Button variant='link' href={ session.publicUrl }>link</Button></li>
                                </ul>
                            </Card.Body>
                            </Accordion.Collapse>
                        </Card>
                    </Accordion>
                </Col>
            );
    });

    return(
        <Container className={classes.View} fluid>
            <Row>
                <Col sm='12'>
                    <h4>{props.workout.name}</h4>
                    <span>{props.workout.description}</span>    
                    {/* <div className={classes.Title}>
                        <h4>{props.workout.name}</h4>
                        <span>{props.workout.description}</span>
                    </div> */}
                </Col>
                <Col sm='12'>
                    <Button variant='primary' onClick={sessionCreateHandler}> &#43; session</Button>
                </Col>
            </Row>
            <Row>
                <div className={classes.Sessions}>
                    <h5 className={classes.SessionTitle}>Sessions</h5>
                    { sessionData }
                </div>
            </Row>
        </Container>
     );
};

const mapStateToProps = state => {
    return {
        workout: state.workouts.view
    }
}

export default connect(mapStateToProps)( workoutview );
