import React, { Component } from 'react';
import Table from 'react-bootstrap/Table';
import Button from 'react-bootstrap/Button';

import { transformUnderscoreCaseToSentenceCase } from '../../../shared/utility';

import classes from './Table.module.css';

class CustomTable extends Component {

    renderTableData = (data, key) => {
        return data.map( values => {
            const tableData = Object.keys(values).map( key => {
                return (
                    <td key={key}>{ values[key] }</td>
                )
            });
            return (
                <tr key={ values[key] }>
                    {tableData}
                    {/* <td key={'action' + key}>
                        <Button variant='link' onClick={this.props.clicked( key )}>view</Button>
                    </td> */}
                </tr>
            );
        });
    };

    renderTableColumnNames = (data, key) => {
        return data.slice(0,1).map( values => {
            const tableColumnNames = Object.keys(values).map( key => {
                return (
                    <th key={key}>{ transformUnderscoreCaseToSentenceCase( key ) }</th>
                )
            });
            return (
                <tr key={values[key]}>
                    {tableColumnNames}
                    {/* <th></th> */}
                </tr>
            );
        });
    }

    render () {

        const table = this.props.data && this.props.data.length > 0 ? (
            <Table striped bordered hover responsive="sm">
                <thead>
                    {this.renderTableColumnNames(this.props.data, this.props.keyValue)}
                </thead>
                <tbody>
                    {this.renderTableData(this.props.data, this.props.keyValue)}
                </tbody>
            </Table>
        ) : <span>Nothing to display!</span>

        return (
            <div className={classes.Table}>
                {this.props.tableHeader}
                {table}
            </div>
        );
    }
}


export default CustomTable;
