import { updateObject } from "../../shared/utility";
import { REQUEST_SUCCESS, SIGNUP_SHOW, REQUEST_FAILED, REQUEST_STARTED } from '../actions/actionTypes';

const initialState = {
    account: null,
    hide: true,
    errors: [],
    toast: null,
    redirect: null,
    loading: false
}

const requestSuccess = ( state, action ) => {
    return updateObject( state, { 
        account: action.account, 
        hide: action.hide,
        toast: action.toast,
        redirect: action.redirect,
        errors: action.errors,
        loading: action.loading
     });
};

const requestStarted = ( state, action ) => {
    return updateObject( state, {
        loading: action.loading
    });
};

const showSignup = ( state, action ) => {
    return updateObject( state, { hide: action.hide });
}

const requestFailed = ( state, action ) => {
    return updateObject( state, {
        errors: action.errors,
        loading: action.loading
    })
}

const reducer = ( state = initialState, action ) => { 
    switch (action.type) {
        case REQUEST_SUCCESS:
            return requestSuccess(state, action);
        case SIGNUP_SHOW:
            return showSignup( state, action );
        case REQUEST_FAILED:
            return requestFailed( state, action );
        case REQUEST_STARTED:
            return requestStarted( state, action );
        default:
            return state;
    }
}

export default reducer;
